mapper.select:
select t.* 
from BDA_AUTH_ROLE_PERMISSION t
where t.NAME != 'Menu' and t.ROLE_ID = #{roleId}

insert.if:
             insert into BDA_AUTH_ROLE_PERMISSION (
             	ROLE_ID,
                PERMISSION,
                ACTION,
                NAME,
                PRIMKEY
                )

                 select
                    #{permission.roleId} as roleId,
                    #{permission.permission} as permission,
                    #{permission.action} as action,
                    #{permission.name} as name,
                    #{permission.primkey} as primkey
                 from dual 
                 where not exists (
	             	select 1 from BDA_AUTH_ROLE_PERMISSION t 
	             	where t.ROLE_ID = #{permission.roleId} 
                    	and t.PERMISSION = #{permission.permission}
                    	and t.ACTION = #{permission.action}
                    	and t.NAME = #{permission.name}
                    	and t.PRIMKEY = #{permission.primkey}
		         )

delete.if:
    		delete from BDA_AUTH_ROLE_PERMISSION t
    		where 

	             (t.ROLE_ID = #{permission.roleId} 
                    	and t.PERMISSION = #{permission.permission}
                    	and t.ACTION = #{permission.action}
                    	and t.NAME = #{permission.name}
                    	and t.PRIMKEY = #{permission.primkey}
		         )
